import React from 'react';

const jobs = [
  {
    title: 'Chuyên viên Massage',
    location: 'TP. Hồ Chí Minh',
    type: 'Toàn thời gian',
    description: 'Có kinh nghiệm massage trị liệu, ưu tiên ứng viên có chứng chỉ nghề.',
    email: 'hr@zunaspa.com'
  },
  {
    title: 'Lễ tân Spa',
    location: 'TP. Hồ Chí Minh',
    type: 'Bán thời gian',
    description: 'Giao tiếp tốt, ngoại hình ưa nhìn, ưu tiên biết tiếng Anh cơ bản.',
    email: 'hr@zunaspa.com'
  },
  {
    title: 'Chuyên viên Chăm sóc da',
    location: 'TP. Hồ Chí Minh',
    type: 'Toàn thời gian',
    description: 'Có kinh nghiệm chăm sóc da, tư vấn liệu trình cho khách hàng.',
    email: 'hr@zunaspa.com'
  }
];

const Careers = () => (
  <div className="min-h-screen bg-spa-gradient">
    <section className="section-padding bg-white">
      <div className="container-custom max-w-3xl mx-auto">
        <h1 className="text-4xl font-serif font-bold text-gray-800 mb-6">Tuyển dụng</h1>
        <p className="text-gray-600 mb-8">Gia nhập đội ngũ Zuna Spa để phát triển sự nghiệp trong môi trường chuyên nghiệp, năng động và thân thiện!</p>
        <div className="space-y-8">
          {jobs.map((job, idx) => (
            <div key={idx} className="card p-6">
              <h2 className="text-2xl font-semibold text-spa-pink-600 mb-2">{job.title}</h2>
              <div className="flex flex-wrap gap-4 text-gray-500 text-sm mb-2">
                <span>Địa điểm: {job.location}</span>
                <span>Hình thức: {job.type}</span>
              </div>
              <p className="text-gray-700 mb-2">{job.description}</p>
              <a href={`mailto:${job.email}`} className="text-spa-pink-600 underline">Ứng tuyển ngay</a>
            </div>
          ))}
        </div>
      </div>
    </section>
  </div>
);

export default Careers;
