import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './components/Navbar';
import Footer from './components/Footer';
import Home from './pages/Home';
import Services from './pages/Services';
import About from './pages/About';
import Contact from './pages/Contact';
import Blog from './pages/Blog';
import BlogDetail from './pages/BlogDetail';
import Booking from './pages/Booking';
import Privacy from './pages/Privacy';
import Terms from './pages/Terms';
import Careers from './pages/Careers';
import Promotions from './pages/Promotions';
import FAQ from './pages/FAQ';
import Reviews from './pages/Reviews';
import ChatWidget from './components/ChatWidget';
import ThemeContext from './context/ThemeContext';

function App() {
  const [theme, setTheme] = useState('pink-pastel');

  const themes = {
    'pink-pastel': {
      primary: 'spa-pink',
      secondary: 'pink-pastel',
      background: 'bg-spa-gradient',
      text: 'text-gray-800'
    },
    'lavender': {
      primary: 'purple',
      secondary: 'lavender',
      background: 'bg-gradient-to-br from-purple-50 to-indigo-50',
      text: 'text-gray-800'
    },
    'mint': {
      primary: 'emerald',
      secondary: 'mint',
      background: 'bg-gradient-to-br from-emerald-50 to-teal-50',
      text: 'text-gray-800'
    },
    'peach': {
      primary: 'orange',
      secondary: 'peach',
      background: 'bg-gradient-to-br from-orange-50 to-red-50',
      text: 'text-gray-800'
    }
  };

  return (
    <ThemeContext.Provider value={{ theme, setTheme, themes }}>
      <Router>
        <div className={`min-h-screen ${themes[theme].background}`}>
          <Navbar />
          <main>
            <Routes>
              <Route path="/" element={<Home />} />
              <Route path="/services" element={<Services />} />
              <Route path="/about" element={<About />} />
              <Route path="/contact" element={<Contact />} />
              <Route path="/blog" element={<Blog />} />
              <Route path="/blog/:id" element={<BlogDetail />} />
              <Route path="/booking" element={<Booking />} />
              <Route path="/privacy" element={<Privacy />} />
              <Route path="/terms" element={<Terms />} />
              <Route path="/careers" element={<Careers />} />
              <Route path="/promotions" element={<Promotions />} />
              <Route path="/faq" element={<FAQ />} />
              <Route path="/reviews" element={<Reviews />} />
            </Routes>
          </main>
          <Footer />
          <ChatWidget />
        </div>
      </Router>
    </ThemeContext.Provider>
  );
}

export default App;
