import React from 'react';

const promotions = [
  {
    title: 'Giảm 20% cho khách hàng mới',
    description: 'Áp dụng cho tất cả dịch vụ massage và chăm sóc da. Đặt lịch ngay để nhận ưu đãi!',
    valid: '01/06/2024 - 30/06/2024'
  },
  {
    title: 'Combo Spa tiết kiệm',
    description: 'Đăng ký gói combo 5 buổi massage, tặng thêm 1 buổi miễn phí.',
    valid: '01/06/2024 - 31/07/2024'
  },
  {
    title: 'Tặng quà sinh nhật',
    description: 'Khách hàng có sinh nhật trong tháng được tặng voucher 200.000đ khi sử dụng dịch vụ bất kỳ.',
    valid: '01/06/2024 - 31/12/2024'
  }
];

const Promotions = () => (
  <div className="min-h-screen bg-spa-gradient">
    <section className="section-padding bg-white">
      <div className="container-custom max-w-3xl mx-auto">
        <h1 className="text-4xl font-serif font-bold text-gray-800 mb-6">Ưu đãi & Khuyến mãi</h1>
        <div className="space-y-8">
          {promotions.map((promo, idx) => (
            <div key={idx} className="card p-6">
              <h2 className="text-2xl font-semibold text-spa-pink-600 mb-2">{promo.title}</h2>
              <p className="text-gray-700 mb-2">{promo.description}</p>
              <div className="text-sm text-gray-500">Thời gian áp dụng: {promo.valid}</div>
            </div>
          ))}
        </div>
      </div>
    </section>
  </div>
);

export default Promotions;
