import React, { useContext } from 'react';
import { ThemeContext } from '../context/ThemeContext';
import CountdownTimer from './CountdownTimer';
import { Link } from 'react-router-dom';

const PromoBar = () => {
  const { theme } = useContext(ThemeContext);
  // V√≠ d·ª•: khuy·∫øn m√£i k·∫øt th√∫c sau 7 ng√†y k·ªÉ t·ª´ b√¢y gi·ªù
  const endDate = new Date();
  endDate.setDate(endDate.getDate() + 7);

  // M√†u n·ªÅn theo theme
  const bgColors = {
    pink: 'bg-pink-500',
    blue: 'bg-blue-500',
    purple: 'bg-purple-500',
    green: 'bg-emerald-500',
    orange: 'bg-orange-500',
  };
  const bg = bgColors[theme] || bgColors.pink;

  return (
    <div className={`w-full ${bg} text-white text-sm sm:text-base flex flex-col sm:flex-row items-center justify-center py-2 px-2 gap-2`}
         style={{minHeight: '40px'}}>
      <span className="font-semibold mr-2">üéâ Khuy·∫øn m√£i ƒë·∫∑c bi·ªát: Gi·∫£m 20% cho t·∫•t c·∫£ d·ªãch v·ª•!</span>
      <span className="hidden sm:inline">|</span>
      <span className="flex items-center gap-1">
        <span className="mr-1">K·∫øt th√∫c sau:</span>
        <CountdownTimer endDate={endDate} compact theme={theme} />
      </span>
      <Link
        to="/booking"
        className="ml-2 bg-white text-gray-900 font-semibold px-3 py-1 rounded hover:bg-gray-100 transition-colors text-xs sm:text-sm"
        style={{whiteSpace: 'nowrap'}}
      >
        ƒê·∫∑t l·ªãch ngay
      </Link>
    </div>
  );
};

export default PromoBar;
