import React from 'react';

const reviews = [
  {
    name: 'Nguyễn Thị Anh',
    content: 'Zuna Spa thực sự là nơi thư giãn tuyệt vời. Nhân viên rất chuyên nghiệp và dịch vụ chất lượng cao.',
    rating: 5
  },
  {
    name: 'Trần Văn Bình',
    content: 'Sau những giờ làm việc căng thẳng, tôi thường đến Zuna Spa để thư giãn. Dịch vụ massage ở đây rất tuyệt vời.',
    rating: 5
  },
  {
    name: 'Lê Thị Cẩm',
    content: 'Chăm sóc da mặt tại Zuna Spa giúp tôi có làn da tươi sáng và tự tin hơn. Rất hài lòng với dịch vụ.',
    rating: 5
  },
  {
    name: 'Phạm Văn Bình',
    content: 'Không gian spa sang trọng, sạch sẽ. Nhân viên thân thiện và tận tâm.',
    rating: 4
  }
];

const Reviews = () => (
  <div className="min-h-screen bg-spa-gradient">
    <section className="section-padding bg-white">
      <div className="container-custom max-w-3xl mx-auto">
        <h1 className="text-4xl font-serif font-bold text-gray-800 mb-6">Đánh giá khách hàng</h1>
        <div className="space-y-8">
          {reviews.map((review, idx) => (
            <div key={idx} className="card p-6">
              <div className="flex items-center mb-2">
                <div className="w-10 h-10 bg-spa-pink-100 rounded-full flex items-center justify-center mr-4">
                  <span className="text-spa-pink-600 font-bold text-lg">{review.name[0]}</span>
                </div>
                <div>
                  <h2 className="font-semibold text-gray-800">{review.name}</h2>
                  <div className="flex">
                    {[...Array(review.rating)].map((_, i) => (
                      <svg key={i} className="w-4 h-4 text-yellow-400" fill="currentColor" viewBox="0 0 20 20">
                        <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
                      </svg>
                    ))}
                  </div>
                </div>
              </div>
              <p className="text-gray-600 mt-2">"{review.content}"</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  </div>
);

export default Reviews;
