import React, { useState } from 'react';

const Reviews = () => {
  const [selectedRating, setSelectedRating] = useState('all');
  const [currentPage, setCurrentPage] = useState(1);

  const reviews = [
    {
      id: 1,
      name: 'Nguyễn Thị Anh',
      rating: 5,
      date: '15/12/2023',
      service: 'Massage Thư Giãn',
      content: 'Zuna Spa thực sự là nơi thư giãn tuyệt vời. Nhân viên rất chuyên nghiệp và dịch vụ chất lượng cao. Tôi đã trải nghiệm massage thư giãn và rất hài lòng với kết quả. Không gian spa rất đẹp và thoải mái.',
      avatar: 'https://images.unsplash.com/photo-1511367461989-f85a21fda167?auto=format&fit=crop&w=100&q=80',
      verified: true
    },
    {
      id: 2,
      name: 'Trần Văn Bình',
      rating: 5,
      date: '12/12/2023',
      service: 'Chăm Sóc Da Mặt',
      content: 'Sau những giờ làm việc căng thẳng, tôi thường đến Zuna Spa để thư giãn. Dịch vụ chăm sóc da mặt ở đây rất tuyệt vời. Chuyên gia rất tận tâm và có kiến thức sâu rộng.',
      avatar: 'https://images.unsplash.com/photo-1508214751196-bcfd4ca60f91?auto=format&fit=crop&w=100&q=80',
      verified: true
    },
    {
      id: 3,
      name: 'Lê Thị Cẩm',
      rating: 4,
      date: '10/12/2023',
      service: 'Trị Liệu Cơ Thể',
      content: 'Chăm sóc da mặt tại Zuna Spa giúp tôi có làn da tươi sáng và tự tin hơn. Rất hài lòng với dịch vụ và sẽ quay lại thường xuyên.',
      avatar: 'https://images.unsplash.com/photo-1517841905240-472988babdf9?auto=format&fit=crop&w=100&q=80',
      verified: true
    },
    {
      id: 4,
      name: 'Phạm Văn Dũng',
      rating: 5,
      date: '08/12/2023',
      service: 'Massage Đá Nóng',
      content: 'Trị liệu cơ thể tại Zuna Spa thực sự hiệu quả. Tôi đã giảm được cơn đau lưng sau vài lần điều trị. Chuyên gia rất giỏi và tận tâm.',
      avatar: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?auto=format&fit=crop&w=100&q=80',
      verified: true
    },
    {
      id: 5,
      name: 'Hoàng Thị Em',
      rating: 4,
      date: '05/12/2023',
      service: 'Yoga Trị Liệu',
      content: 'Massage đá nóng là trải nghiệm tuyệt vời. Đá nóng giúp thư giãn sâu và giảm stress rất hiệu quả. Không gian spa rất yên tĩnh và thoải mái.',
      avatar: 'https://images.unsplash.com/photo-1494790108755-2616b612b786?auto=format&fit=crop&w=100&q=80',
      verified: false
    },
    {
      id: 6,
      name: 'Vũ Văn Phúc',
      rating: 5,
      date: '03/12/2023',
      service: 'Thiền Định',
      content: 'Yoga trị liệu tại Zuna Spa giúp tôi cải thiện sức khỏe rất nhiều. Giáo viên yoga rất chuyên nghiệp và tận tâm. Rất hài lòng với dịch vụ.',
      avatar: 'https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?auto=format&fit=crop&w=100&q=80',
      verified: true
    },
    {
      id: 7,
      name: 'Đỗ Thị Gia',
      rating: 4,
      date: '01/12/2023',
      service: 'Chăm Sóc Da Mặt',
      content: 'Thiền định tại Zuna Spa giúp tôi tĩnh tâm và giảm stress rất hiệu quả. Không gian thiền rất yên tĩnh và thoải mái. Rất hài lòng với trải nghiệm.',
      avatar: 'https://images.unsplash.com/photo-1438761681033-6461ffad8d80?auto=format&fit=crop&w=100&q=80',
      verified: true
    },
    {
      id: 8,
      name: 'Ngô Văn Hùng',
      rating: 5,
      date: '28/11/2023',
      service: 'Massage Thư Giãn',
      content: 'Dịch vụ chăm sóc da mặt tại Zuna Spa rất chuyên nghiệp. Chuyên gia có kiến thức sâu rộng và tư vấn rất hữu ích. Làn da của tôi đã cải thiện rõ rệt.',
      avatar: 'https://images.unsplash.com/photo-1500648767791-00dcc994a43e?auto=format&fit=crop&w=100&q=80',
      verified: true
    }
  ];

  const ratingStats = {
    total: reviews.length,
    average: 4.6,
    distribution: {
      5: reviews.filter(r => r.rating === 5).length,
      4: reviews.filter(r => r.rating === 4).length,
      3: reviews.filter(r => r.rating === 3).length,
      2: reviews.filter(r => r.rating === 2).length,
      1: reviews.filter(r => r.rating === 1).length
    }
  };

  const filteredReviews = selectedRating === 'all' 
    ? reviews 
    : reviews.filter(review => review.rating === parseInt(selectedRating));

  const reviewsPerPage = 6;
  const totalPages = Math.ceil(filteredReviews.length / reviewsPerPage);
  const startIndex = (currentPage - 1) * reviewsPerPage;
  const paginatedReviews = filteredReviews.slice(startIndex, startIndex + reviewsPerPage);

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, i) => (
      <svg
        key={i}
        className={`w-5 h-5 ${i < rating ? 'text-yellow-400' : 'text-gray-300'}`}
        fill="currentColor"
        viewBox="0 0 20 20"
      >
        <path d="M9.049 2.927c.3-.921 1.603-.921 1.902 0l1.07 3.292a1 1 0 00.95.69h3.462c.969 0 1.371 1.24.588 1.81l-2.8 2.034a1 1 0 00-.364 1.118l1.07 3.292c.3.921-.755 1.688-1.54 1.118l-2.8-2.034a1 1 0 00-1.175 0l-2.8 2.034c-.784.57-1.838-.197-1.539-1.118l1.07-3.292a1 1 0 00-.364-1.118L2.98 8.72c-.783-.57-.38-1.81.588-1.81h3.461a1 1 0 00.951-.69l1.07-3.292z" />
      </svg>
    ));
  };

  return (
    <div className="min-h-screen">
      {/* Hero Section */}
      <section className="relative py-32 bg-gradient-to-r from-spa-pink-50 to-pink-pastel-50">
        <div className="container-custom">
          <div className="text-center max-w-4xl mx-auto">
            <h1 className="text-5xl lg:text-6xl font-serif font-bold text-gray-800 mb-8">
              Đánh giá từ khách hàng
            </h1>
            <p className="text-xl lg:text-2xl text-gray-600 leading-relaxed mb-12">
              Những đánh giá chân thực từ khách hàng đã trải nghiệm dịch vụ tại Zuna Spa
            </p>
            
            {/* Rating Overview */}
            <div className="bg-white rounded-2xl shadow-lg p-8 max-w-2xl mx-auto">
              <div className="flex items-center justify-center mb-6">
                <div className="text-center">
                  <div className="text-5xl font-bold text-gray-800 mb-2">{ratingStats.average}</div>
                  <div className="flex justify-center mb-2">
                    {renderStars(Math.round(ratingStats.average))}
                  </div>
                  <div className="text-gray-600">Dựa trên {ratingStats.total} đánh giá</div>
                </div>
              </div>
              
              <div className="space-y-2">
                {[5, 4, 3, 2, 1].map((rating) => (
                  <div key={rating} className="flex items-center">
                    <span className="w-8 text-sm text-gray-600">{rating} sao</span>
                    <div className="flex-1 mx-4 bg-gray-200 rounded-full h-2">
                      <div 
                        className="bg-yellow-400 h-2 rounded-full"
                        style={{ width: `${(ratingStats.distribution[rating] / ratingStats.total) * 100}%` }}
                      ></div>
                    </div>
                    <span className="w-12 text-sm text-gray-600">{ratingStats.distribution[rating]}</span>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      </section>

      {/* Filter Section */}
      <section className="py-16 bg-white border-b border-gray-100">
        <div className="container-custom">
          <div className="flex flex-wrap justify-center gap-4">
            <button
              onClick={() => setSelectedRating('all')}
              className={`px-8 py-3 rounded-full font-medium transition-all duration-300 ${
                selectedRating === 'all'
                  ? 'bg-spa-pink-600 text-white shadow-lg'
                  : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
              }`}
            >
              Tất cả ({reviews.length})
            </button>
            {[5, 4, 3, 2, 1].map((rating) => (
              <button
                key={rating}
                onClick={() => setSelectedRating(rating.toString())}
                className={`px-8 py-3 rounded-full font-medium transition-all duration-300 ${
                  selectedRating === rating.toString()
                    ? 'bg-spa-pink-600 text-white shadow-lg'
                    : 'bg-gray-100 text-gray-700 hover:bg-gray-200'
                }`}
              >
                {rating} sao ({ratingStats.distribution[rating]})
              </button>
            ))}
          </div>
        </div>
      </section>

      {/* Reviews Grid */}
      <section className="py-20 bg-white">
        <div className="container-custom">
          {paginatedReviews.length > 0 ? (
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
              {paginatedReviews.map((review) => (
                <div key={review.id} className="bg-white rounded-2xl shadow-lg p-8 border border-gray-100 hover:shadow-xl transition-shadow">
                  <div className="flex items-center mb-6">
                    <img 
                      src={review.avatar}
                      alt={review.name}
                      className="w-16 h-16 rounded-full object-cover mr-4 shadow-lg"
                    />
                    <div className="flex-1">
                      <div className="flex items-center mb-1">
                        <h3 className="font-semibold text-gray-800 text-lg">{review.name}</h3>
                        {review.verified && (
                          <svg className="w-5 h-5 text-spa-pink-600 ml-2" fill="currentColor" viewBox="0 0 20 20">
                            <path fillRule="evenodd" d="M6.267 3.455a3.066 3.066 0 001.745-.723 3.066 3.066 0 013.976 0 3.066 3.066 0 001.745.723 3.066 3.066 0 012.812 2.812c.051.643.304 1.254.723 1.745a3.066 3.066 0 010 3.976 3.066 3.066 0 00-.723 1.745 3.066 3.066 0 01-2.812 2.812 3.066 3.066 0 00-1.745.723 3.066 3.066 0 01-3.976 0 3.066 3.066 0 00-1.745-.723 3.066 3.066 0 01-2.812-2.812 3.066 3.066 0 00-.723-1.745 3.066 3.066 0 010-3.976 3.066 3.066 0 00.723-1.745 3.066 3.066 0 012.812-2.812zm7.44 5.252a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z" clipRule="evenodd" />
                          </svg>
                        )}
                      </div>
                      <div className="flex items-center text-sm text-gray-500 mb-2">
                        <span>{review.date}</span>
                        <span className="mx-2">•</span>
                        <span>{review.service}</span>
                      </div>
                      <div className="flex">
                        {renderStars(review.rating)}
                      </div>
                    </div>
                  </div>
                  
                  <p className="text-gray-600 leading-relaxed">
                    "{review.content}"
                  </p>
                </div>
              ))}
            </div>
          ) : (
            <div className="text-center py-16">
              <svg className="w-16 h-16 text-gray-400 mx-auto mb-4" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M8 12h.01M12 12h.01M16 12h.01M21 12c0 4.418-4.03 8-9 8a9.863 9.863 0 01-4.255-.949L3 20l1.395-3.72C3.512 15.042 3 13.574 3 12c0-4.418 4.03-8 9-8s9 3.582 9 8z" />
              </svg>
              <p className="text-xl text-gray-600">Không có đánh giá nào phù hợp.</p>
            </div>
          )}

          {/* Pagination */}
          {totalPages > 1 && (
            <div className="flex justify-center mt-12">
              <div className="flex space-x-2">
                <button
                  onClick={() => setCurrentPage(prev => Math.max(prev - 1, 1))}
                  disabled={currentPage === 1}
                  className="px-4 py-2 rounded-lg border border-gray-300 text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                  Trước
                </button>
                
                {Array.from({ length: totalPages }, (_, i) => i + 1).map((page) => (
                  <button
                    key={page}
                    onClick={() => setCurrentPage(page)}
                    className={`px-4 py-2 rounded-lg border transition-colors ${
                      currentPage === page
                        ? 'bg-spa-pink-600 text-white border-spa-pink-600'
                        : 'border-gray-300 text-gray-700 hover:bg-gray-50'
                    }`}
                  >
                    {page}
                  </button>
                ))}
                
                <button
                  onClick={() => setCurrentPage(prev => Math.min(prev + 1, totalPages))}
                  disabled={currentPage === totalPages}
                  className="px-4 py-2 rounded-lg border border-gray-300 text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                >
                  Sau
                </button>
              </div>
            </div>
          )}
        </div>
      </section>

      {/* CTA Section */}
      <section className="py-20 bg-gradient-to-r from-spa-pink-500 to-pink-pastel-400">
        <div className="container-custom text-center">
          <h2 className="text-4xl lg:text-5xl font-serif font-bold text-white mb-6">
            Sẵn sàng trải nghiệm?
          </h2>
          <p className="text-xl text-white/90 mb-12 max-w-2xl mx-auto">
            Đặt lịch ngay hôm nay để tận hưởng dịch vụ spa chất lượng cao tại Zuna Spa
          </p>
          <div className="flex flex-col sm:flex-row gap-6 justify-center">
            <a href="/booking" className="bg-white text-spa-pink-600 hover:bg-gray-100 font-semibold px-8 py-4 rounded-lg transition-colors text-lg">
              Đặt lịch ngay
            </a>
            <a href="/contact" className="border-2 border-white text-white hover:bg-white hover:text-spa-pink-600 font-semibold px-8 py-4 rounded-lg transition-colors text-lg">
              Liên hệ tư vấn
            </a>
          </div>
        </div>
      </section>
    </div>
  );
};

export default Reviews;
