import React, { useState, useContext } from 'react';
import { Link, useLocation } from 'react-router-dom';
import ThemeContext from '../context/ThemeContext';

const Navbar = () => {
  const [isOpen, setIsOpen] = useState(false);
  const { theme, setTheme, themes } = useContext(ThemeContext);
  const location = useLocation();

  const navigation = [
    { name: 'Trang chủ', href: '/' },
    { name: 'Dịch vụ', href: '/services' },
    { name: 'Về chúng tôi', href: '/about' },
    { name: 'Blog', href: '/blog' },
    { name: 'Ưu đãi', href: '/promotions' },
    { name: 'Tuyển dụng', href: '/careers' },
    { name: 'FAQ', href: '/faq' },
    { name: 'Đánh giá', href: '/reviews' },
    { name: 'Liên hệ', href: '/contact' },
  ];

  const themeOptions = [
    { name: 'Hồng Pastel', value: 'pink-pastel' },
    { name: 'Lavender', value: 'lavender' },
    { name: 'Mint', value: 'mint' },
    { name: 'Peach', value: 'peach' },
  ];

  return (
    <nav className="bg-white/90 backdrop-blur-md shadow-md sticky top-0 z-50">
      <div className="container-custom flex justify-between items-center py-5">
        {/* Logo */}
        <Link to="/" className="font-serif text-3xl font-bold text-gradient tracking-wide select-none">
          Zuna Spa
        </Link>
        {/* Desktop Navigation */}
        <div className="hidden md:flex items-center space-x-6 lg:space-x-10">
          {navigation.map((item) => (
            <Link
              key={item.name}
              to={item.href}
              className={`font-medium px-2 py-1 rounded transition-colors duration-200 ${
                location.pathname === item.href
                  ? 'text-spa-pink-600 border-b-2 border-spa-pink-600 bg-pink-pastel-50'
                  : 'text-gray-700 hover:text-spa-pink-500 hover:bg-pink-pastel-50'
              }`}
            >
              {item.name}
            </Link>
          ))}
        </div>
        {/* Theme Switcher & CTA */}
        <div className="hidden md:flex items-center space-x-2">
          <div className="relative group">
            <button className="p-2 rounded-lg bg-pink-pastel-100 hover:bg-pink-pastel-200 transition-colors">
              <svg className="w-5 h-5 text-spa-pink-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M7 21a4 4 0 01-4-4V5a2 2 0 012-2h4a2 2 0 012 2v12a4 4 0 01-4 4zM21 5a2 2 0 00-2-2h-4a2 2 0 00-2 2v12a4 4 0 004 4h4a2 2 0 002-2V5z" />
              </svg>
            </button>
            <div className="absolute right-0 mt-2 w-48 bg-white rounded-lg shadow-xl opacity-0 invisible group-hover:opacity-100 group-hover:visible transition-all duration-200">
              <div className="py-2">
                {themeOptions.map((option) => (
                  <button
                    key={option.value}
                    onClick={() => setTheme(option.value)}
                    className={`w-full text-left px-4 py-2 text-sm hover:bg-pink-pastel-50 transition-colors ${
                      theme === option.value ? 'bg-pink-pastel-100 text-spa-pink-600' : 'text-gray-700'
                    }`}
                  >
                    {option.name}
                  </button>
                ))}
              </div>
            </div>
          </div>
        </div>
        {/* Mobile menu button */}
        <div className="md:hidden">
          <button
            onClick={() => setIsOpen(!isOpen)}
            className="text-gray-600 hover:text-spa-pink-500 focus:outline-none"
          >
            <svg className="w-7 h-7" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              {isOpen ? (
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
              ) : (
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
              )}
            </svg>
          </button>
        </div>
      </div>
      {/* Mobile Navigation */}
      {isOpen && (
        <div className="md:hidden bg-white rounded-b-lg shadow-lg">
          <div className="px-4 pt-4 pb-3 space-y-1">
            {navigation.map((item) => (
              <Link
                key={item.name}
                to={item.href}
                className={`block px-3 py-2 rounded-md text-base font-medium transition-colors duration-200 ${
                  location.pathname === item.href
                    ? 'bg-spa-pink-100 text-spa-pink-600'
                    : 'text-gray-700 hover:bg-pink-pastel-50 hover:text-spa-pink-500'
                }`}
                onClick={() => setIsOpen(false)}
              >
                {item.name}
              </Link>
            ))}
          </div>
        </div>
      )}
    </nav>
  );
};

export default Navbar;
