import React, { useState, useEffect } from 'react';

const CountdownTimer = () => {
  const [timeLeft, setTimeLeft] = useState({
    days: 0,
    hours: 0,
    minutes: 0,
    seconds: 0
  });

  // Set end date (30 days from now)
  const endDate = new Date();
  endDate.setDate(endDate.getDate() + 30);

  useEffect(() => {
    const timer = setInterval(() => {
      const now = new Date().getTime();
      const distance = endDate.getTime() - now;

      if (distance > 0) {
        setTimeLeft({
          days: Math.floor(distance / (1000 * 60 * 60 * 24)),
          hours: Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60)),
          minutes: Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60)),
          seconds: Math.floor((distance % (1000 * 60)) / 1000)
        });
      } else {
        setTimeLeft({ days: 0, hours: 0, minutes: 0, seconds: 0 });
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [endDate]);

  const TimeUnit = ({ value, label }) => (
    <div className="text-center">
      <div className="bg-white rounded-lg shadow-lg p-3 mx-1">
        <div className="text-2xl font-bold text-spa-pink-600">{value.toString().padStart(2, '0')}</div>
        <div className="text-xs text-gray-600 uppercase">{label}</div>
      </div>
    </div>
  );

  return (
    <div className="bg-gradient-to-r from-spa-pink-500 to-pink-pastel-400 text-white py-8">
      <div className="container-custom">
        <div className="text-center">
          <h2 className="text-3xl font-serif font-bold mb-2">Khuyến mãi đặc biệt</h2>
          <p className="text-xl mb-6 opacity-90">Giảm 20% cho tất cả dịch vụ - Chỉ còn lại</p>
          
          <div className="flex justify-center items-center mb-6">
            <TimeUnit value={timeLeft.days} label="Ngày" />
            <div className="text-2xl font-bold mx-2">:</div>
            <TimeUnit value={timeLeft.hours} label="Giờ" />
            <div className="text-2xl font-bold mx-2">:</div>
            <TimeUnit value={timeLeft.minutes} label="Phút" />
            <div className="text-2xl font-bold mx-2">:</div>
            <TimeUnit value={timeLeft.seconds} label="Giây" />
          </div>
          
          <button className="bg-white text-spa-pink-600 hover:bg-gray-100 font-medium py-3 px-8 rounded-lg transition-colors">
            Đặt lịch ngay
          </button>
        </div>
      </div>
    </div>
  );
};

export default CountdownTimer;
