import React from 'react';

const faqs = [
  {
    question: 'Làm thế nào để đặt lịch dịch vụ?',
    answer: 'Bạn có thể đặt lịch trực tiếp tại spa, qua điện thoại hoặc online trên website của chúng tôi.'
  },
  {
    question: 'Có cần đặt lịch trước không?',
    answer: 'Chúng tôi khuyến nghị đặt lịch trước ít nhất 1 ngày để đảm bảo có slot phù hợp với lịch trình của bạn.'
  },
  {
    question: 'Có chính sách hủy lịch không?',
    answer: 'Có, bạn có thể hủy lịch miễn phí trước 24 giờ. Hủy trong vòng 24 giờ sẽ có phí hủy.'
  },
  {
    question: 'Có dịch vụ tại nhà không?',
    answer: 'Hiện tại chúng tôi chưa cung cấp dịch vụ tại nhà, nhưng đang trong quá trình phát triển.'
  },
  {
    question: 'Có bãi đỗ xe không?',
    answer: 'Zuna Spa có bãi đỗ xe miễn phí cho khách hàng.'
  }
];

const FAQ = () => (
  <div className="min-h-screen bg-spa-gradient">
    <section className="section-padding bg-white">
      <div className="container-custom max-w-3xl mx-auto">
        <h1 className="text-4xl font-serif font-bold text-gray-800 mb-6">Câu hỏi thường gặp</h1>
        <div className="space-y-6">
          {faqs.map((faq, idx) => (
            <div key={idx} className="card p-6">
              <h2 className="text-lg font-semibold text-gray-800 mb-2">{faq.question}</h2>
              <p className="text-gray-600">{faq.answer}</p>
            </div>
          ))}
        </div>
      </div>
    </section>
  </div>
);

export default FAQ;
